import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Recipe } from '../../types';
import api from '../../services/api';
import { Button, Input, TextArea, Card, CardBody, CardHeader } from '../common';

export function RecipeRunner() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [recipe, setRecipe] = useState<Recipe | null>(null);
  const [inputValues, setInputValues] = useState<Record<string, string>>({});
  const [isLoading, setIsLoading] = useState(true);
  const [isStarting, setIsStarting] = useState(false);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    if (id) {
      loadRecipe(parseInt(id));
    }
  }, [id]);

  const loadRecipe = async (recipeId: number) => {
    setIsLoading(true);
    try {
      const data = await api.getRecipe(recipeId);
      setRecipe(data);

      // Initialize input values
      const initialValues: Record<string, string> = {};
      (data.required_inputs || []).forEach((input) => {
        initialValues[input] = '';
      });
      setInputValues(initialValues);
    } catch (err: any) {
      setError(err.message);
    } finally {
      setIsLoading(false);
    }
  };

  const handleStartExecution = async () => {
    if (!recipe) return;

    // Check for missing required inputs
    const missingInputs = (recipe.required_inputs || []).filter(
      (input) => !inputValues[input]?.trim()
    );

    if (missingInputs.length > 0) {
      setError(`Please fill in all required fields: ${missingInputs.join(', ')}`);
      return;
    }

    setIsStarting(true);
    setError(null);
    try {
      const result = await api.startExecution(recipe.id, inputValues);
      navigate(`/executions/${result.executionId}`);
    } catch (err: any) {
      setError(err.message);
      setIsStarting(false);
    }
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  if (!recipe) {
    return (
      <div className="text-center py-12">
        <p className="text-secondary-600">Recipe not found</p>
        <Button onClick={() => navigate('/')} className="mt-4">
          Go to Dashboard
        </Button>
      </div>
    );
  }

  return (
    <div className="max-w-2xl mx-auto space-y-6">
      {/* Header */}
      <div>
        <h1 className="text-2xl font-bold text-secondary-900">{recipe.name}</h1>
        {recipe.description && (
          <p className="text-secondary-600 mt-1">{recipe.description}</p>
        )}
      </div>

      {error && (
        <div className="bg-red-50 text-red-700 px-4 py-3 rounded-lg">
          {error}
        </div>
      )}

      {/* Recipe Overview */}
      <Card>
        <CardHeader>
          <h2 className="font-semibold text-secondary-900">Workflow Steps</h2>
        </CardHeader>
        <CardBody className="p-0">
          <div className="divide-y divide-secondary-100">
            {recipe.steps?.map((step, index) => (
              <div key={index} className="px-6 py-3 flex items-center space-x-4">
                <div className="flex-shrink-0 w-8 h-8 bg-primary-100 text-primary-700 rounded-full flex items-center justify-center font-semibold">
                  {index + 1}
                </div>
                <div>
                  <h4 className="font-medium text-secondary-900">{step.step_name}</h4>
                  <p className="text-sm text-secondary-500">{step.ai_model}</p>
                </div>
              </div>
            ))}
          </div>
        </CardBody>
      </Card>

      {/* Input Form */}
      {(recipe.required_inputs?.length || 0) > 0 && (
        <Card>
          <CardHeader>
            <h2 className="font-semibold text-secondary-900">Input Values</h2>
            <p className="text-sm text-secondary-500 mt-1">
              Provide the required inputs to start the workflow
            </p>
          </CardHeader>
          <CardBody className="space-y-4">
            {recipe.required_inputs?.map((input) => {
              const isLongInput = input.toLowerCase().includes('content') ||
                input.toLowerCase().includes('description') ||
                input.toLowerCase().includes('details');

              return isLongInput ? (
                <TextArea
                  key={input}
                  label={formatLabel(input)}
                  value={inputValues[input] || ''}
                  onChange={(e) =>
                    setInputValues({ ...inputValues, [input]: e.target.value })
                  }
                  placeholder={`Enter ${formatLabel(input).toLowerCase()}...`}
                  rows={4}
                />
              ) : (
                <Input
                  key={input}
                  label={formatLabel(input)}
                  value={inputValues[input] || ''}
                  onChange={(e) =>
                    setInputValues({ ...inputValues, [input]: e.target.value })
                  }
                  placeholder={`Enter ${formatLabel(input).toLowerCase()}...`}
                />
              );
            })}
          </CardBody>
        </Card>
      )}

      {/* Actions */}
      <div className="flex items-center justify-between">
        <Button variant="ghost" onClick={() => navigate('/')}>
          Cancel
        </Button>
        <Button onClick={handleStartExecution} isLoading={isStarting}>
          Start Workflow
        </Button>
      </div>
    </div>
  );
}

function formatLabel(varName: string): string {
  return varName
    .replace(/_/g, ' ')
    .replace(/\b\w/g, (c) => c.toUpperCase());
}
