import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Recipe, WorkflowExecution } from '../../types';
import api from '../../services/api';
import { Button, Card, CardBody } from '../common';
import { useLanguage } from '../../context/LanguageContext';

export function Dashboard() {
  const navigate = useNavigate();
  const { t } = useLanguage();
  const [recipes, setRecipes] = useState<Recipe[]>([]);
  const [recentExecutions, setRecentExecutions] = useState<WorkflowExecution[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    setIsLoading(true);
    setError(null);
    try {
      const [recipesData, executionsData] = await Promise.all([
        api.getRecipes(),
        api.getExecutions(),
      ]);
      setRecipes(recipesData);
      setRecentExecutions(executionsData.slice(0, 5));
    } catch (err: any) {
      setError(err.message);
    } finally {
      setIsLoading(false);
    }
  };

  const handleCloneRecipe = async (recipeId: number) => {
    try {
      const cloned = await api.cloneRecipe(recipeId);
      navigate(`/recipes/${cloned.id}`);
    } catch (err: any) {
      setError(err.message);
    }
  };

  const handleDeleteRecipe = async (recipeId: number) => {
    if (!window.confirm('Are you sure you want to delete this recipe?')) return;
    try {
      await api.deleteRecipe(recipeId);
      setRecipes(recipes.filter(r => r.id !== recipeId));
    } catch (err: any) {
      setError(err.message);
    }
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  const templateRecipes = recipes.filter(r => r.is_template);
  const userRecipes = recipes.filter(r => !r.is_template);

  return (
    <div className="space-y-8">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold text-secondary-900">{t('dashboardTitle')}</h1>
          <p className="text-secondary-600 mt-1">
            {t('dashboardSubtitle')}
          </p>
        </div>
        <Button onClick={() => navigate('/recipes/new')}>
          {t('createNewRecipe')}
        </Button>
      </div>

      {error && (
        <div className="bg-red-50 text-red-700 px-4 py-3 rounded-lg">
          {error}
        </div>
      )}

      {/* Template Recipes */}
      <section>
        <h2 className="text-lg font-semibold text-secondary-900 mb-4">
          {t('starterTemplates')}
        </h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {templateRecipes.map((recipe) => (
            <RecipeCard
              key={recipe.id}
              recipe={recipe}
              onRun={() => navigate(`/recipes/${recipe.id}/run`)}
              onClone={() => handleCloneRecipe(recipe.id)}
              isTemplate
              t={t}
            />
          ))}
        </div>
      </section>

      {/* User Recipes */}
      <section>
        <h2 className="text-lg font-semibold text-secondary-900 mb-4">
          {t('myRecipes')}
        </h2>
        {userRecipes.length === 0 ? (
          <Card>
            <CardBody className="text-center py-12">
              <p className="text-secondary-600 mb-4">
                {t('noRecipesYet')}
              </p>
              <Button onClick={() => navigate('/recipes/new')}>
                {t('createFirstRecipe')}
              </Button>
            </CardBody>
          </Card>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {userRecipes.map((recipe) => (
              <RecipeCard
                key={recipe.id}
                recipe={recipe}
                onRun={() => navigate(`/recipes/${recipe.id}/run`)}
                onEdit={() => navigate(`/recipes/${recipe.id}`)}
                onClone={() => handleCloneRecipe(recipe.id)}
                onDelete={() => handleDeleteRecipe(recipe.id)}
                t={t}
              />
            ))}
          </div>
        )}
      </section>

      {/* Recent Executions */}
      <section>
        <div className="flex items-center justify-between mb-4">
          <h2 className="text-lg font-semibold text-secondary-900">
            {t('recentExecutions')}
          </h2>
          {recentExecutions.length > 0 && (
            <Button variant="ghost" onClick={() => navigate('/executions')}>
              {t('viewAll')}
            </Button>
          )}
        </div>
        {recentExecutions.length === 0 ? (
          <Card>
            <CardBody className="text-center py-8">
              <p className="text-secondary-600">
                {t('noExecutionsYet')}
              </p>
            </CardBody>
          </Card>
        ) : (
          <div className="space-y-2">
            {recentExecutions.map((execution) => (
              <ExecutionRow
                key={execution.id}
                execution={execution}
                onClick={() => navigate(`/executions/${execution.id}`)}
                t={t}
              />
            ))}
          </div>
        )}
      </section>
    </div>
  );
}

interface RecipeCardProps {
  recipe: Recipe;
  onRun: () => void;
  onEdit?: () => void;
  onClone: () => void;
  onDelete?: () => void;
  isTemplate?: boolean;
  t: (key: any) => string;
}

function RecipeCard({ recipe, onRun, onEdit, onClone, onDelete, isTemplate, t }: RecipeCardProps) {
  return (
    <Card hoverable className="flex flex-col">
      <CardBody className="flex-1">
        <div className="flex items-start justify-between">
          <div>
            <h3 className="font-semibold text-secondary-900">{recipe.name}</h3>
            {isTemplate && (
              <span className="inline-block mt-1 px-2 py-0.5 text-xs font-medium bg-primary-100 text-primary-700 rounded">
                {t('template')}
              </span>
            )}
          </div>
          <span className="text-sm text-secondary-500">
            {recipe.step_count || 0} {t('steps')}
          </span>
        </div>
        <p className="text-sm text-secondary-600 mt-2 line-clamp-2">
          {recipe.description || t('noDescription')}
        </p>
      </CardBody>
      <div className="px-6 py-3 border-t border-secondary-100 flex items-center justify-between">
        <Button size="sm" onClick={onRun}>
          {t('run')}
        </Button>
        <div className="flex items-center space-x-2">
          {onEdit && (
            <Button size="sm" variant="ghost" onClick={onEdit}>
              {t('edit')}
            </Button>
          )}
          <Button size="sm" variant="ghost" onClick={onClone}>
            {t('clone')}
          </Button>
          {onDelete && (
            <Button size="sm" variant="ghost" onClick={onDelete}>
              {t('delete')}
            </Button>
          )}
        </div>
      </div>
    </Card>
  );
}

interface ExecutionRowProps {
  execution: WorkflowExecution;
  onClick: () => void;
  t: (key: any) => string;
}

function ExecutionRow({ execution, onClick, t }: ExecutionRowProps) {
  const statusColors: Record<string, string> = {
    pending: 'bg-secondary-100 text-secondary-700',
    running: 'bg-blue-100 text-blue-700',
    paused: 'bg-yellow-100 text-yellow-700',
    completed: 'bg-green-100 text-green-700',
    failed: 'bg-red-100 text-red-700',
  };

  const statusLabels: Record<string, string> = {
    pending: t('pending'),
    running: t('running'),
    paused: t('paused'),
    completed: t('completed'),
    failed: t('failed'),
  };

  return (
    <Card hoverable onClick={onClick}>
      <CardBody className="flex items-center justify-between py-3">
        <div className="flex items-center space-x-4">
          <span className={`px-2 py-1 text-xs font-medium rounded ${statusColors[execution.status]}`}>
            {statusLabels[execution.status] || execution.status}
          </span>
          <span className="font-medium text-secondary-900">
            {execution.recipe_name || `Recipe #${execution.recipe_id}`}
          </span>
        </div>
        <div className="flex items-center space-x-4 text-sm text-secondary-500">
          <span>
            {t('currentStep')} {execution.current_step}/{execution.total_steps || '?'}
          </span>
          <span>
            {new Date(execution.created_at).toLocaleDateString()}
          </span>
        </div>
      </CardBody>
    </Card>
  );
}
