import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Recipe, RecipeStep, AIModel } from '../../types';
import api from '../../services/api';
import { Button, Input, TextArea, Select, Card, CardBody, CardHeader, Modal } from '../common';
import { useLanguage } from '../../context/LanguageContext';

const DEFAULT_STEP: Omit<RecipeStep, 'id' | 'recipe_id' | 'created_at'> = {
  step_order: 1,
  step_name: 'New Step',
  ai_model: 'mock',
  prompt_template: '',
  output_format: 'text',
  model_config: JSON.stringify({ temperature: 0.7, maxTokens: 2000 }),
};

export function RecipeRunner() {
  const { id } = useParams();
  const navigate = useNavigate();
  const { t } = useLanguage();

  const [recipe, setRecipe] = useState<Recipe | null>(null);
  const [localSteps, setLocalSteps] = useState<RecipeStep[]>([]);
  const [inputValues, setInputValues] = useState<Record<string, string>>({});
  const [models, setModels] = useState<AIModel[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isStarting, setIsStarting] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [expandedSteps, setExpandedSteps] = useState<Set<number>>(new Set([0]));
  const [showVariableHelp, setShowVariableHelp] = useState(false);

  useEffect(() => {
    loadModels();
    if (id) {
      loadRecipe(parseInt(id));
    }
  }, [id]);

  const loadModels = async () => {
    try {
      const { all } = await api.getAIModels();
      setModels(all);
    } catch (err: any) {
      console.error('Failed to load models:', err);
    }
  };

  const loadRecipe = async (recipeId: number) => {
    setIsLoading(true);
    try {
      const data = await api.getRecipe(recipeId);
      setRecipe(data);
      setLocalSteps(data.steps || []);

      // Initialize input values
      const initialValues: Record<string, string> = {};
      (data.required_inputs || []).forEach((input) => {
        initialValues[input] = '';
      });
      setInputValues(initialValues);
    } catch (err: any) {
      setError(err.message);
    } finally {
      setIsLoading(false);
    }
  };

  // Extract required inputs from current step prompts
  const extractRequiredInputs = (): string[] => {
    const inputs = new Set<string>();
    const variableRegex = /\{\{([^}]+)\}\}/g;

    const standardNames = [
      'brand_voice', 'amazon_requirements', 'social_media_guidelines',
      'image_style_guidelines', 'platform_requirements', 'tone_guidelines',
    ];

    for (const step of localSteps) {
      let match;
      while ((match = variableRegex.exec(step.prompt_template)) !== null) {
        const varName = match[1].trim();
        // Skip step outputs and company standards
        if (!varName.match(/^step_\d+_output$/) &&
            !standardNames.some(s => varName.toLowerCase().includes(s.replace(/_/g, '')))) {
          inputs.add(varName);
        }
      }
    }
    return Array.from(inputs);
  };

  const requiredInputs = extractRequiredInputs();

  const updateStep = (index: number, updates: Partial<RecipeStep>) => {
    const newSteps = [...localSteps];
    newSteps[index] = { ...newSteps[index], ...updates };
    setLocalSteps(newSteps);
  };

  const addStep = () => {
    const newStep: RecipeStep = {
      ...DEFAULT_STEP,
      step_order: localSteps.length + 1,
      step_name: `Step ${localSteps.length + 1}`,
    } as RecipeStep;
    setLocalSteps([...localSteps, newStep]);
    setExpandedSteps(new Set([...expandedSteps, localSteps.length]));
  };

  const removeStep = (index: number) => {
    if (localSteps.length <= 1) {
      setError('Recipe must have at least one step');
      return;
    }
    const newSteps = localSteps.filter((_, i) => i !== index);
    // Renumber steps
    newSteps.forEach((step, i) => {
      step.step_order = i + 1;
    });
    setLocalSteps(newSteps);
  };

  const moveStep = (index: number, direction: 'up' | 'down') => {
    const newIndex = direction === 'up' ? index - 1 : index + 1;
    if (newIndex < 0 || newIndex >= localSteps.length) return;

    const newSteps = [...localSteps];
    [newSteps[index], newSteps[newIndex]] = [newSteps[newIndex], newSteps[index]];
    // Renumber steps
    newSteps.forEach((step, i) => {
      step.step_order = i + 1;
    });
    setLocalSteps(newSteps);
  };

  const toggleStepExpanded = (index: number) => {
    const newExpanded = new Set(expandedSteps);
    if (newExpanded.has(index)) {
      newExpanded.delete(index);
    } else {
      newExpanded.add(index);
    }
    setExpandedSteps(newExpanded);
  };

  const handleStartExecution = async () => {
    if (!recipe) return;

    // Check for missing required inputs
    const missingInputs = requiredInputs.filter(
      (input) => !inputValues[input]?.trim()
    );

    if (missingInputs.length > 0) {
      setError(`Please fill in all required fields: ${missingInputs.join(', ')}`);
      return;
    }

    // Check that all steps have prompts
    const emptyPromptSteps = localSteps.filter(s => !s.prompt_template.trim());
    if (emptyPromptSteps.length > 0) {
      setError('All steps must have a prompt template');
      return;
    }

    setIsStarting(true);
    setError(null);
    try {
      // Pass modified steps to the execution
      const result = await api.startExecution(recipe.id, inputValues, localSteps);
      navigate(`/executions/${result.executionId}`);
    } catch (err: any) {
      setError(err.message);
      setIsStarting(false);
    }
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  if (!recipe) {
    return (
      <div className="text-center py-12">
        <p className="text-secondary-600">{t('recipeNotFound')}</p>
        <Button onClick={() => navigate('/')} className="mt-4">
          {t('goToDashboard')}
        </Button>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <div className="flex items-center space-x-2">
            <h1 className="text-2xl font-bold text-secondary-900">{recipe.name}</h1>
            {recipe.is_template && (
              <span className="px-2 py-0.5 text-xs font-medium bg-primary-100 text-primary-700 rounded">
                {t('template')}
              </span>
            )}
          </div>
          {recipe.description && (
            <p className="text-secondary-600 mt-1">{recipe.description}</p>
          )}
        </div>
        <div className="flex items-center space-x-3">
          <Button variant="ghost" onClick={() => navigate('/')}>
            {t('cancel')}
          </Button>
          <Button onClick={handleStartExecution} isLoading={isStarting}>
            {t('startWorkflow')}
          </Button>
        </div>
      </div>

      {error && (
        <div className="bg-red-50 text-red-700 px-4 py-3 rounded-lg">
          {error}
        </div>
      )}

      {/* Input Values */}
      {requiredInputs.length > 0 && (
        <Card>
          <CardHeader>
            <h2 className="font-semibold text-secondary-900">{t('inputValues')}</h2>
            <p className="text-sm text-secondary-500 mt-1">
              {t('provideInputs')}
            </p>
          </CardHeader>
          <CardBody className="space-y-4">
            {requiredInputs.map((input) => {
              const isLongInput = input.toLowerCase().includes('content') ||
                input.toLowerCase().includes('description') ||
                input.toLowerCase().includes('details');

              return isLongInput ? (
                <TextArea
                  key={input}
                  label={formatLabel(input)}
                  value={inputValues[input] || ''}
                  onChange={(e) =>
                    setInputValues({ ...inputValues, [input]: e.target.value })
                  }
                  placeholder={`Enter ${formatLabel(input).toLowerCase()}...`}
                  rows={4}
                />
              ) : (
                <Input
                  key={input}
                  label={formatLabel(input)}
                  value={inputValues[input] || ''}
                  onChange={(e) =>
                    setInputValues({ ...inputValues, [input]: e.target.value })
                  }
                  placeholder={`Enter ${formatLabel(input).toLowerCase()}...`}
                />
              );
            })}
          </CardBody>
        </Card>
      )}

      {/* Workflow Steps */}
      <div>
        <div className="flex items-center justify-between mb-4">
          <div>
            <h2 className="text-lg font-semibold text-secondary-900">{t('workflowSteps')}</h2>
            <p className="text-sm text-secondary-500">
              {t('customizePrompts')}
            </p>
          </div>
          <div className="flex items-center space-x-2">
            <button
              onClick={() => setShowVariableHelp(true)}
              className="text-sm text-primary-600 hover:text-primary-700"
            >
              {t('variableHelp')}
            </button>
            <Button size="sm" onClick={addStep}>
              {t('addStep')}
            </Button>
          </div>
        </div>

        <div className="space-y-4">
          {localSteps.map((step, index) => (
            <Card key={index}>
              <div
                className="px-6 py-4 flex items-center justify-between cursor-pointer hover:bg-secondary-50"
                onClick={() => toggleStepExpanded(index)}
              >
                <div className="flex items-center space-x-4">
                  <div className="flex-shrink-0 w-8 h-8 bg-primary-100 text-primary-700 rounded-full flex items-center justify-center font-semibold">
                    {index + 1}
                  </div>
                  <div>
                    <h4 className="font-medium text-secondary-900">{step.step_name}</h4>
                    <p className="text-sm text-secondary-500">
                      {models.find(m => m.id === step.ai_model)?.name || step.ai_model}
                    </p>
                  </div>
                </div>
                <div className="flex items-center space-x-2">
                  <button
                    onClick={(e) => { e.stopPropagation(); moveStep(index, 'up'); }}
                    disabled={index === 0}
                    className="p-1 text-secondary-400 hover:text-secondary-600 disabled:opacity-30"
                  >
                    <ChevronUpIcon className="w-4 h-4" />
                  </button>
                  <button
                    onClick={(e) => { e.stopPropagation(); moveStep(index, 'down'); }}
                    disabled={index === localSteps.length - 1}
                    className="p-1 text-secondary-400 hover:text-secondary-600 disabled:opacity-30"
                  >
                    <ChevronDownIcon className="w-4 h-4" />
                  </button>
                  <button
                    onClick={(e) => { e.stopPropagation(); removeStep(index); }}
                    className="p-1 text-red-400 hover:text-red-600"
                  >
                    <TrashIcon className="w-4 h-4" />
                  </button>
                  <ChevronIcon
                    className={`w-5 h-5 text-secondary-400 transition-transform ${
                      expandedSteps.has(index) ? 'transform rotate-180' : ''
                    }`}
                  />
                </div>
              </div>

              {expandedSteps.has(index) && (
                <CardBody className="border-t border-secondary-100 space-y-4">
                  <Input
                    label="Step Name"
                    value={step.step_name}
                    onChange={(e) => updateStep(index, { step_name: e.target.value })}
                    placeholder="e.g., Research Product"
                  />

                  <Select
                    label="AI Model"
                    value={step.ai_model}
                    onChange={(e) => updateStep(index, { ai_model: e.target.value })}
                    options={models.map(m => ({
                      value: m.id,
                      label: `${m.name}${m.available ? '' : ' (Not configured)'}`,
                    }))}
                  />

                  <div>
                    <label className="block text-sm font-medium text-secondary-700 mb-1">
                      Prompt Template
                    </label>
                    <TextArea
                      value={step.prompt_template}
                      onChange={(e) => updateStep(index, { prompt_template: e.target.value })}
                      placeholder="Enter your prompt template here. Use {{variable_name}} for variables."
                      rows={8}
                      className="font-mono text-sm"
                    />
                    <p className="mt-1 text-sm text-secondary-500">
                      Use {'{{variable_name}}'} for user inputs, {'{{step_N_output}}'} for previous step outputs
                    </p>
                  </div>

                  <Select
                    label="Output Format"
                    value={step.output_format}
                    onChange={(e) => updateStep(index, { output_format: e.target.value as any })}
                    options={[
                      { value: 'text', label: 'Plain Text' },
                      { value: 'markdown', label: 'Markdown' },
                      { value: 'json', label: 'JSON' },
                    ]}
                  />

                  {/* Model Config */}
                  <div className="border-t border-secondary-200 pt-4">
                    <h3 className="text-sm font-medium text-secondary-700 mb-3">Model Settings</h3>
                    <div className="grid grid-cols-2 gap-4">
                      <Input
                        label="Temperature"
                        type="number"
                        min="0"
                        max="2"
                        step="0.1"
                        value={JSON.parse(step.model_config || '{}').temperature || 0.7}
                        onChange={(e) => {
                          const config = JSON.parse(step.model_config || '{}');
                          config.temperature = parseFloat(e.target.value);
                          updateStep(index, { model_config: JSON.stringify(config) });
                        }}
                      />
                      <Input
                        label="Max Tokens"
                        type="number"
                        min="100"
                        max="100000"
                        step="100"
                        value={JSON.parse(step.model_config || '{}').maxTokens || 2000}
                        onChange={(e) => {
                          const config = JSON.parse(step.model_config || '{}');
                          config.maxTokens = parseInt(e.target.value);
                          updateStep(index, { model_config: JSON.stringify(config) });
                        }}
                      />
                    </div>
                  </div>
                </CardBody>
              )}
            </Card>
          ))}
        </div>
      </div>

      {/* Bottom Actions */}
      <div className="flex items-center justify-between pt-4 border-t border-secondary-200">
        <Button variant="ghost" onClick={() => navigate('/')}>
          Cancel
        </Button>
        <Button onClick={handleStartExecution} isLoading={isStarting} size="lg">
          Start Workflow
        </Button>
      </div>

      {/* Variable Help Modal */}
      <Modal
        isOpen={showVariableHelp}
        onClose={() => setShowVariableHelp(false)}
        title="Variable Reference"
        size="lg"
      >
        <div className="space-y-4">
          <div>
            <h3 className="font-semibold text-secondary-900">User Input Variables</h3>
            <p className="text-sm text-secondary-600 mt-1">
              Use <code className="bg-secondary-100 px-1 rounded">{'{{variable_name}}'}</code> to create input fields.
            </p>
            <div className="mt-2 bg-secondary-50 p-3 rounded text-sm font-mono">
              {'{{product_name}}'}<br/>
              {'{{target_audience}}'}<br/>
              {'{{additional_context}}'}
            </div>
          </div>
          <div>
            <h3 className="font-semibold text-secondary-900">Previous Step Outputs</h3>
            <p className="text-sm text-secondary-600 mt-1">
              Reference output from previous steps using <code className="bg-secondary-100 px-1 rounded">{'{{step_N_output}}'}</code>.
            </p>
            <div className="mt-2 bg-secondary-50 p-3 rounded text-sm font-mono">
              {'{{step_1_output}}'}<br/>
              {'{{step_2_output}}'}
            </div>
          </div>
          <div>
            <h3 className="font-semibold text-secondary-900">Company Standards</h3>
            <p className="text-sm text-secondary-600 mt-1">
              Inject your company standards into prompts.
            </p>
            <div className="mt-2 bg-secondary-50 p-3 rounded text-sm font-mono">
              {'{{brand_voice}}'}<br/>
              {'{{amazon_requirements}}'}<br/>
              {'{{image_style_guidelines}}'}
            </div>
          </div>
        </div>
      </Modal>
    </div>
  );
}

function formatLabel(varName: string): string {
  return varName
    .replace(/_/g, ' ')
    .replace(/\b\w/g, (c) => c.toUpperCase());
}

// Icons
function ChevronUpIcon({ className }: { className?: string }) {
