import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Recipe, RecipeStep, AIModel } from '../../types';
import api from '../../services/api';
import { Button, Input, TextArea, Select, Card, CardBody, CardHeader, Modal } from '../common';
import { useLanguage } from '../../context/LanguageContext';

const DEFAULT_STEP: Omit<RecipeStep, 'id' | 'recipe_id' | 'created_at'> = {
  step_order: 1,
  step_name: 'New Step',
  ai_model: 'mock',
  prompt_template: '',
  output_format: 'text',
  model_config: JSON.stringify({ temperature: 0.7, maxTokens: 2000 }),
};

export function RecipeBuilder() {
  const { id } = useParams();
  const navigate = useNavigate();
  const { t } = useLanguage();
  // Check if this is a new recipe: either no id param or id is 'new'
  const isNew = !id || id === 'new';

  const [recipe, setRecipe] = useState<Partial<Recipe>>({
    name: '',
    description: '',
    steps: [],
  });
  const [models, setModels] = useState<AIModel[]>([]);
  const [isLoading, setIsLoading] = useState(!isNew);
  const [isSaving, setIsSaving] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [selectedStepIndex, setSelectedStepIndex] = useState<number | null>(null);
  const [showVariableHelp, setShowVariableHelp] = useState(false);

  useEffect(() => {
    loadModels();
    if (!isNew && id) {
      loadRecipe(parseInt(id));
    }
  }, [id, isNew]);

  const loadModels = async () => {
    try {
      const { all } = await api.getAIModels();
      setModels(all);
    } catch (err: any) {
      console.error('Failed to load models:', err);
    }
  };

  const loadRecipe = async (recipeId: number) => {
    setIsLoading(true);
    try {
      const data = await api.getRecipe(recipeId);
      setRecipe(data);
      if (data.steps && data.steps.length > 0) {
        setSelectedStepIndex(0);
      }
    } catch (err: any) {
      setError(err.message);
    } finally {
      setIsLoading(false);
    }
  };

  const handleSave = async () => {
    if (!recipe.name) {
      setError('Recipe name is required');
      return;
    }

    setIsSaving(true);
    setError(null);
    try {
      const steps = (recipe.steps || []).map((step, index) => ({
        ...step,
        step_order: index + 1,
      }));

      if (isNew) {
        const created = await api.createRecipe({
          name: recipe.name,
          description: recipe.description,
          steps,
        });
        navigate(`/recipes/${created.id}`);
      } else if (id) {
        await api.updateRecipe(parseInt(id), {
          name: recipe.name,
          description: recipe.description,
          steps,
        });
      }
    } catch (err: any) {
      setError(err.message);
    } finally {
      setIsSaving(false);
    }
  };

  const addStep = () => {
    const newStep = {
      ...DEFAULT_STEP,
      step_order: (recipe.steps?.length || 0) + 1,
      step_name: `Step ${(recipe.steps?.length || 0) + 1}`,
    };
    setRecipe({
      ...recipe,
      steps: [...(recipe.steps || []), newStep as RecipeStep],
    });
    setSelectedStepIndex((recipe.steps?.length || 0));
  };

  const updateStep = (index: number, updates: Partial<RecipeStep>) => {
    const steps = [...(recipe.steps || [])];
    steps[index] = { ...steps[index], ...updates };
    setRecipe({ ...recipe, steps });
  };

  const removeStep = (index: number) => {
    const steps = (recipe.steps || []).filter((_, i) => i !== index);
    setRecipe({ ...recipe, steps });
    if (selectedStepIndex === index) {
      setSelectedStepIndex(steps.length > 0 ? Math.max(0, index - 1) : null);
    } else if (selectedStepIndex !== null && selectedStepIndex > index) {
      setSelectedStepIndex(selectedStepIndex - 1);
    }
  };

  const moveStep = (index: number, direction: 'up' | 'down') => {
    const steps = [...(recipe.steps || [])];
    const newIndex = direction === 'up' ? index - 1 : index + 1;
    if (newIndex < 0 || newIndex >= steps.length) return;

    [steps[index], steps[newIndex]] = [steps[newIndex], steps[index]];
    setRecipe({ ...recipe, steps });
    setSelectedStepIndex(newIndex);
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  const selectedStep = selectedStepIndex !== null ? recipe.steps?.[selectedStepIndex] : null;

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold text-secondary-900">
            {isNew ? 'Create New Recipe' : 'Edit Recipe'}
          </h1>
          <p className="text-secondary-600 mt-1">
            Build your AI workflow step by step
          </p>
        </div>
        <div className="flex items-center space-x-3">
          <Button variant="ghost" onClick={() => navigate('/')}>
            Cancel
          </Button>
          <Button onClick={handleSave} isLoading={isSaving}>
            {isNew ? 'Create Recipe' : 'Save Changes'}
          </Button>
        </div>
      </div>

      {error && (
        <div className="bg-red-50 text-red-700 px-4 py-3 rounded-lg">
          {error}
        </div>
      )}

      {/* Recipe Info */}
      <Card>
        <CardHeader>
          <h2 className="font-semibold text-secondary-900">Recipe Details</h2>
        </CardHeader>
        <CardBody className="space-y-4">
          <Input
            label="Recipe Name"
            value={recipe.name || ''}
            onChange={(e) => setRecipe({ ...recipe, name: e.target.value })}
            placeholder="e.g., Product Listing Generator"
          />
          <TextArea
            label="Description"
            value={recipe.description || ''}
            onChange={(e) => setRecipe({ ...recipe, description: e.target.value })}
            placeholder="Describe what this recipe does..."
            rows={3}
          />
        </CardBody>
      </Card>

      {/* Steps Builder */}
      <div className="grid grid-cols-3 gap-6">
        {/* Step List */}
        <div className="col-span-1">
          <Card>
            <CardHeader className="flex items-center justify-between">
              <h2 className="font-semibold text-secondary-900">Steps</h2>
              <Button size="sm" onClick={addStep}>
                Add Step
              </Button>
            </CardHeader>
            <CardBody className="p-0">
              {recipe.steps?.length === 0 ? (
                <div className="p-6 text-center text-secondary-500">
                  No steps yet. Add a step to get started.
                </div>
              ) : (
                <div className="divide-y divide-secondary-100">
                  {recipe.steps?.map((step, index) => (
                    <div
                      key={index}
                      className={`p-4 cursor-pointer transition-colors ${
                        selectedStepIndex === index
                          ? 'bg-primary-50'
                          : 'hover:bg-secondary-50'
                      }`}
                      onClick={() => setSelectedStepIndex(index)}
                    >
                      <div className="flex items-center justify-between">
                        <div>
                          <span className="text-sm font-medium text-secondary-500">
                            Step {index + 1}
                          </span>
                          <h4 className="font-medium text-secondary-900">
                            {step.step_name}
                          </h4>
                        </div>
                        <div className="flex items-center space-x-1">
                          <button
                            onClick={(e) => { e.stopPropagation(); moveStep(index, 'up'); }}
                            disabled={index === 0}
                            className="p-1 text-secondary-400 hover:text-secondary-600 disabled:opacity-30"
                          >
                            <ChevronUpIcon className="w-4 h-4" />
                          </button>
                          <button
                            onClick={(e) => { e.stopPropagation(); moveStep(index, 'down'); }}
                            disabled={index === (recipe.steps?.length || 0) - 1}
                            className="p-1 text-secondary-400 hover:text-secondary-600 disabled:opacity-30"
                          >
                            <ChevronDownIcon className="w-4 h-4" />
                          </button>
                          <button
                            onClick={(e) => { e.stopPropagation(); removeStep(index); }}
                            className="p-1 text-red-400 hover:text-red-600"
                          >
                            <TrashIcon className="w-4 h-4" />
                          </button>
                        </div>
                      </div>
                      <p className="text-sm text-secondary-500 mt-1">
                        {models.find(m => m.id === step.ai_model)?.name || step.ai_model}
                      </p>
                    </div>
                  ))}
                </div>
              )}
            </CardBody>
          </Card>
        </div>

        {/* Step Editor */}
        <div className="col-span-2">
          {selectedStep ? (
            <Card>
              <CardHeader>
                <h2 className="font-semibold text-secondary-900">
                  Step {selectedStepIndex! + 1} Configuration
                </h2>
              </CardHeader>
              <CardBody className="space-y-4">
                <Input
                  label="Step Name"
                  value={selectedStep.step_name}
                  onChange={(e) => updateStep(selectedStepIndex!, { step_name: e.target.value })}
                  placeholder="e.g., Research Product"
                />

                <Select
                  label="AI Model"
                  value={selectedStep.ai_model}
                  onChange={(e) => updateStep(selectedStepIndex!, { ai_model: e.target.value })}
                  options={models.map(m => ({
                    value: m.id,
                    label: `${m.name}${m.available ? '' : ' (Not configured)'}`,
                  }))}
                />

                <div>
                  <div className="flex items-center justify-between mb-1">
                    <label className="block text-sm font-medium text-secondary-700">
                      Prompt Template
                    </label>
                    <button
                      onClick={() => setShowVariableHelp(true)}
                      className="text-sm text-primary-600 hover:text-primary-700"
                    >
                      Variable Help
                    </button>
                  </div>
                  <TextArea
                    value={selectedStep.prompt_template}
                    onChange={(e) => updateStep(selectedStepIndex!, { prompt_template: e.target.value })}
                    placeholder="Enter your prompt template here. Use {{variable_name}} for variables."
                    rows={12}
                    className="font-mono text-sm"
                  />
                  <p className="mt-1 text-sm text-secondary-500">
                    Use {'{{variable_name}}'} for user inputs, {'{{step_N_output}}'} for previous step outputs
                  </p>
                </div>

                <Select
                  label="Output Format"
                  value={selectedStep.output_format}
                  onChange={(e) => updateStep(selectedStepIndex!, { output_format: e.target.value as any })}
                  options={[
                    { value: 'text', label: 'Plain Text' },
                    { value: 'markdown', label: 'Markdown' },
                    { value: 'json', label: 'JSON' },
                  ]}
                />

                {/* Model Config */}
                <div className="border-t border-secondary-200 pt-4">
                  <h3 className="text-sm font-medium text-secondary-700 mb-3">Model Settings</h3>
                  <div className="grid grid-cols-2 gap-4">
                    <Input
                      label="Temperature"
                      type="number"
                      min="0"
                      max="2"
                      step="0.1"
                      value={JSON.parse(selectedStep.model_config || '{}').temperature || 0.7}
                      onChange={(e) => {
                        const config = JSON.parse(selectedStep.model_config || '{}');
                        config.temperature = parseFloat(e.target.value);
                        updateStep(selectedStepIndex!, { model_config: JSON.stringify(config) });
                      }}
                    />
                    <Input
                      label="Max Tokens"
                      type="number"
                      min="100"
                      max="100000"
                      step="100"
                      value={JSON.parse(selectedStep.model_config || '{}').maxTokens || 2000}
                      onChange={(e) => {
                        const config = JSON.parse(selectedStep.model_config || '{}');
                        config.maxTokens = parseInt(e.target.value);
                        updateStep(selectedStepIndex!, { model_config: JSON.stringify(config) });
                      }}
                    />
                  </div>
                </div>
              </CardBody>
            </Card>
          ) : (
            <Card>
              <CardBody className="text-center py-12">
                <p className="text-secondary-600">
                  Select a step to edit or add a new step to get started.
                </p>
              </CardBody>
            </Card>
          )}
        </div>
      </div>

      {/* Variable Help Modal */}
      <Modal
        isOpen={showVariableHelp}
        onClose={() => setShowVariableHelp(false)}
        title="Variable Reference"
        size="lg"
      >
        <div className="space-y-4">
          <div>
            <h3 className="font-semibold text-secondary-900">User Input Variables</h3>
            <p className="text-sm text-secondary-600 mt-1">
              Use <code className="bg-secondary-100 px-1 rounded">{'{{variable_name}}'}</code> to create input fields.
            </p>
            <div className="mt-2 bg-secondary-50 p-3 rounded text-sm font-mono">
              {'{{product_name}}'}<br/>
              {'{{target_audience}}'}<br/>
              {'{{additional_context}}'}
            </div>
          </div>
          <div>
            <h3 className="font-semibold text-secondary-900">Previous Step Outputs</h3>
            <p className="text-sm text-secondary-600 mt-1">
              Reference output from previous steps using <code className="bg-secondary-100 px-1 rounded">{'{{step_N_output}}'}</code>.
            </p>
            <div className="mt-2 bg-secondary-50 p-3 rounded text-sm font-mono">
              {'{{step_1_output}}'}<br/>
              {'{{step_2_output}}'}
            </div>
          </div>
          <div>
            <h3 className="font-semibold text-secondary-900">Company Standards</h3>
            <p className="text-sm text-secondary-600 mt-1">
              Inject your company standards into prompts.
            </p>
            <div className="mt-2 bg-secondary-50 p-3 rounded text-sm font-mono">
              {'{{brand_voice}}'}<br/>
              {'{{amazon_requirements}}'}<br/>
              {'{{image_style_guidelines}}'}
            </div>
          </div>
        </div>
      </Modal>
    </div>
  );
}

// Icons
function ChevronUpIcon({ className }: { className?: string }) {
  return (
    <svg className={className} fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 15l7-7 7 7" />
    </svg>
  );
}

function ChevronDownIcon({ className }: { className?: string }) {
  return (
    <svg className={className} fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
    </svg>
  );
}

function TrashIcon({ className }: { className?: string }) {
  return (
    <svg className={className} fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" />
    </svg>
  );
}
